let ponto=document.getElementById("some-nav"),cabecalho=document.getElementById("cabecalho");document.addEventListener("scroll",function(){let e=cabecalho.getBoundingClientRect().top;ponto.getBoundingClientRect().top<e?((cabecalho=document.getElementById("cabecalho")).classList.add("bg"),navLogo=document.getElementById("nav-logo"),navLogo.classList.remove("navbar--none"),btnAssine=document.getElementById("btn-assine"),btnAssine.classList.remove("navbar--none")):(cabecalho.classList.remove("bg"),navLogo.classList.add("navbar--none"),btnAssine.classList.add("navbar--none"))});